const gameStore = new Vuex.Store({
  state:{
    gameData: {},
    isGameStarted: false,
    isGamePaused: false,
    showNextLevel: false,
    isAllWordsFound: false,
    score: 0,
    level: 0,
    bonusScore: 2000,
    dropArea: [],
    error: {exist: 'Already Found', invalid: 'Not a word'},
    fetchedData: ''

  },
  getters:{

  },
  mutations:{
    resetGame: function(state){
      state.isGameStarted = false;
      state.gameData = {};
      state.dropArea = [];
      state.level = 0;
      state.showNextLevel = false;
      state.isAllWordsFound = false;
      state.isGamePaused = false;
    },
    nextLevel: function(state, payload){
      state.isGameStarted = true;
      state.gameData = {c: payload};
      state.dropArea = [];
      state.level = state.level + 1;
      state.showNextLevel = false;
      state.isAllWordsFound = false;
    },
    updateScore: function(state, payload){
      state.score = state.score + payload;

      //Bonus points
      if(state.gameData.c.userAnswers.length === state.gameData.c.opt.length){
        state.score = state.score + state.bonusScore;
      }
    },

    letterUp: function(state, payload){
      var i = state.dropArea.indexOf('');
      if(i > -1){
        Vue.set(state.dropArea, i, payload.value); // same as `state.dropArea.splice(i, 1, payload.value)`
      }else{
        state.dropArea.push(payload.value);
      }
    },
    letterDown: function(state, payload){

      var i = state.dropArea.indexOf(payload.value);
      if(i === state.dropArea.length - 1){
        state.dropArea.pop();
      }else if(i !== -1){
        Vue.set(state.dropArea, i, ''); // same as `state.dropArea.splice(i, 1, '')`
      }

    },
    arrayCleanUpDropArea: function(state){
      state.dropArea.pop();
    },
    lastWord: function(state, payload){
      state.gameData.c.lastword = payload.value;
    },
    validWord: function(state, payload){
      state.gameData.c.userAnswers.push(payload.value);
      if(payload.value.length > 4) state.showNextLevel = true;

      if(state.gameData.c.userAnswers.length === state.gameData.c.opt.length){
        state.isAllWordsFound = true;
      }
    },
    pauseGame: function(state, payload){
      state.isGamePaused = !payload;
    }

  },
  actions:{
    letterUp: function(context, payload){
      context.commit('letterUp', payload);
    },
    letterDown: function(context, payload){
      context.commit('letterDown', payload);
    },
    lastWord: function(context, payload){
      context.commit('lastWord', payload);
    },
    validWord:function(context, payload){
      context.commit('validWord', payload);
    },
    arrayCleanUpDropArea:function(context, payload){
      context.commit('arrayCleanUpDropArea', payload);
    },
    nextLevel: function(context, payload){
      context.commit('nextLevel', payload);
    },
    updateScore: function(context, payload){
      context.commit('updateScore', payload);
    },
    pauseGame: function(context, payload){
      context.commit('pauseGame', payload);
    },
    resetGame: function(context){
      context.commit('resetGame');
    }
  }
});


